function showForm() {
    if(document.getElementById('cta-btn')) {
        document.getElementById('cta-btn').style.display = 'none';
    }
    if(document.getElementById('email-form')) {
        document.getElementById('email-form').style.display = 'block';
    }
}

function changeLang(lang) {
    const t = translations[lang] || translations['en'];
    const elements = {
        'main-subtitle': 'subtitle',
        'section-title': 'secTitle',
        'section-desc': 'secDesc',
        'radius-text': 'radius',
        'card1-title': 'c1t',
        'card1-desc': 'c1d',
        'card2-title': 'c2t',
        'card2-desc': 'c2d',
        'cta-title': 'ctaT',
        'cta-btn': 'ctaB',
        'email-placeholder': 'mailPH',
        'submit-text': 'mailSub',
        'nav-home': 'navHome',
        'nav-info': 'navInfo',
        'nav-qa': 'navQA',
        'qa-title': 'qaTitle',
        'qa-1-q': 'qa1q',
        'qa-1-a': 'qa1a',
        'qa-2-q': 'qa2q',
        'qa-2-a': 'qa2a',
        'qa-form-title': 'qaFormTitle',
        'qa-form-placeholder': 'qaFormPlaceholder',
        'qa-form-submit': 'qaFormSubmit',
        'how-it-works-title': 'howItWorksTitle',
        'step1-title': 'step1Title',
        'step1-desc': 'step1Desc',
        'step2-title': 'step2Title',
        'step2-desc': 'step2Desc',
        'step3-title': 'step3Title',
        'step3-desc': 'step3Desc',
        'features-title': 'featuresTitle',
        'feature1': 'feature1',
        'feature2': 'feature2',
        'feature3': 'feature3',
        'feature4': 'feature4',
        'about-us-title': 'aboutUsTitle',
        'about-us-desc': 'aboutUsDesc',
        'info-title': 'infoTitle',
        'faq-title': 'faqTitle',
        'faq1-q': 'faq1q',
        'faq1-a': 'faq1a',
        'faq2-q': 'faq2q',
        'faq2-a': 'faq2a',
        'faq3-q': 'faq3q',
        'faq3-a': 'faq3a',
        'faq4-q': 'faq4q',
        'faq4-a': 'faq4a',
        'company-title': 'companyTitle',
        'company-desc': 'companyDesc',
        'privacy-title': 'privacyTitle',
        'privacy-desc': 'privacyDesc',
        'terms-title': 'termsTitle',
        'terms-desc': 'termsDesc'
    };

    for (const id in elements) {
        const element = document.getElementById(id);
        if (element) {
            if (element.tagName === 'INPUT' || element.tagName === 'TEXTAREA') {
                element.placeholder = t[elements[id]];
            } else if (['privacy-desc', 'terms-desc'].includes(id)) {
                element.innerHTML = t[elements[id]];
            } else {
                element.innerText = t[elements[id]];
            }
        }
    }
    localStorage.setItem('lang', lang);
}


// Dark Mode Toggle
const themeSwitch = document.querySelector('#checkbox');

themeSwitch.addEventListener('change', () => {
  document.body.classList.toggle('dark-mode');
  if (document.body.classList.contains('dark-mode')) {
    localStorage.setItem('theme', 'dark');
  } else {
    localStorage.setItem('theme', 'light');
  }
});

// On page load
const currentTheme = localStorage.getItem('theme');
if (currentTheme) {
    document.body.classList.add(currentTheme + '-mode');
    if (currentTheme === 'dark') {
        themeSwitch.checked = true;
        document.body.classList.add('dark-mode');
    }
}

const currentLang = localStorage.getItem('lang') || 'ko';
const langSelector = document.getElementById('lang-selector');
if (langSelector) {
    langSelector.value = currentLang;
    langSelector.addEventListener('change', (event) => {
        changeLang(event.target.value);
    });
}
changeLang(currentLang);
